function getLoadMediaSrc(medias) {
  const res = medias.map((item, i) => {
    const nodeType = item.tagName;
    return new createjs.LoadItem().set({
      id: `${i}-${nodeType}`,
      src: item.getAttribute("data-preload"),
      type: createjs.AbstractLoader.BLOB,
    });
  });
  return res;
}

